// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./Verifier.sol"; // Import the Verifier contract
import "./Pairing.sol";  // Import the Pairing library

contract Wallet {
    using Pairing for Pairing.G1Point; // Use Pairing for its G1Point utilities

    // Mapping to store user balances
    mapping(address => uint256) public balances;

    // Instance of the Verifier contract
    Verifier public verifier;

    // Constructor to initialize the Verifier contract instance
    constructor(address verifierAddress) {
        verifier = Verifier(verifierAddress);
    }

    // Deposit Ether into the wallet
    function deposit() public payable {
        balances[msg.sender] += msg.value;
    }

    // Withdraw Ether with zk-SNARK proof validation
    function withdraw(
        uint256 amount,                   // Amount to withdraw
        Pairing.G1Point memory a,         // G1Point `a` from proof
        Pairing.G2Point memory b,         // G2Point `b` from proof
        Pairing.G1Point memory c,         // G1Point `c` from proof,
        uint[2] memory input              // Public inputs for the proof
    ) public {
        require(balances[msg.sender] >= amount, "Insufficient balance"); // Check sufficient balance

        // Create the proof structure to send to Verifier
        Verifier.Proof memory proof = Verifier.Proof(a, b, c);

        // Validate the zk-SNARK proof using the Verifier contract
        require(verifier.verifyTx(proof, input), "ZKP Verification Failed");

        // Update user balance after successful proof verification
        balances[msg.sender] -= amount;

        // Transfer the funds to the sender
        payable(msg.sender).transfer(amount);
    }

    // Read the balance of the user
    function getBalance() public view returns (uint256) {
        return balances[msg.sender];
    }
}

